import { useState } from 'react';


function UserHome() {
  const [isAnimating, setIsAnimating] = useState(true);

  const handleAnimationEnd = () => {
    setIsAnimating(false);
    document.querySelector('.container').style.backgroundImage = `url(path/to/image.jpg)`;
  };

  return (
    <div className="container">
      {isAnimating && (
        <div className="circle" onAnimationEnd={handleAnimationEnd}></div>
      )}
    </div>
  );
}

export default UserHome;
