import { useState, useEffect } from 'react';
import './App.css';

function UderHome() {
  const [isAnimating, setIsAnimating] = useState(true);
  const [bgImage, setBgImage] = useState('');

  const handleAnimationEnd = () => {
    setIsAnimating(false);
  };

  useEffect(() => {
    if (!isAnimating) {
      setBgImage(`url(https://images.unsplash.com/photo-1634704784915-aacf363b021f?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=2070&q=80)`);
    }
  }, [isAnimating]);

  return (
    <div className="container" style={{ backgroundImage: bgImage }}>
      {isAnimating && (
        <div className="circle" onAnimationEnd={handleAnimationEnd}></div>
      )}
    </div>
  );
}

export default UderHome;
